fn solution_1() {
    let ret = INPUT.chars().fold(0, |acc, elem| {
        if elem == '(' {
            return acc + 1;
        } else {
            return acc - 1;
        }
    });
    solution!(ret);
}

fn solution_2() {
    let mut floor = 0;
    for (i, char) in INPUT.chars().enumerate() {
        floor += match char {
            '(' => 1,
            ')' => -1,
            _ => unreachable!(),
        };
        if floor < 0 {
            solution!(i + 1);
            break;
        }
    }
}

pub fn run_day() {
    solution_1();
    solution_2();
}
